import React, { useState, useRef, useEffect } from 'react';
import './css/MainContent.css';

const MainContent = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const loadingRef = useRef(null);
  const [searchCount, setSearchCount] = useState(0);
  const [backgroundClass, setBackgroundClass] = useState('default');
  const [previousBackground, setPreviousBackground] = useState(null);

  // This ensures the loading animation remounts with every search
  const loadingKey = `loading-${searchCount}`;

  const handleSearch = (e) => {
    e.preventDefault();
    if (city.trim() === '') return;

    // First, always increment search count to ensure proper remounting
    setSearchCount(prevCount => prevCount + 1);

    // Save the current background before starting to load
    const currentBg = backgroundClass;

    // Then set loading state
    setLoading(true);
    setError(null);

    // Simulate API call
    setTimeout(() => {
      const descriptions = ['Sunny', 'Cloudy', 'Rainy', 'Partly Cloudy'];
      const description = descriptions[Math.floor(Math.random() * 4)];

      if (loadingRef.current) {
        // Add fade-out class
        loadingRef.current.classList.add('fade-out');

        // Listen for animation completion
        const handleAnimationEnd = () => {
          if (loadingRef.current) {
            loadingRef.current.removeEventListener('animationend', handleAnimationEnd);
          }

          // Set the previous background to maintain during transition
          setPreviousBackground(currentBg);

          // End loading state
          setLoading(false);

          // Update weather data with new background
          setWeatherData({
            city: city,
            temperature: Math.floor(Math.random() * 30) + 5,
            description: description,
            humidity: Math.floor(Math.random() * 50) + 30,
            windSpeed: Math.floor(Math.random() * 20) + 1,
          });

          // Update background class - this will trigger the transition
          setBackgroundClass(description);

          // After transition completes, clear the previous background
          setTimeout(() => {
            setPreviousBackground(null);
          }, 1500);  // Match this with CSS transition time
        };

        // Add the event listener
        loadingRef.current.addEventListener('animationend', handleAnimationEnd);
      } else {
        // Fallback in case ref isn't available
        setPreviousBackground(currentBg);
        setLoading(false);
        setWeatherData({
          city: city,
          temperature: Math.floor(Math.random() * 30) + 5,
          description: description,
          humidity: Math.floor(Math.random() * 50) + 30,
          windSpeed: Math.floor(Math.random() * 20) + 1,
        });
        setBackgroundClass(description);

        setTimeout(() => {
          setPreviousBackground(null);
        }, 1500);
      }
    }, 1500);
  };

  return (
    <div className="wrapper">
      {/* Default background */}
      <div className={`background default ${backgroundClass !== 'default' ? 'fade-out' : ''}`}></div>

      {/* Weather background - dynamic class based on weather */}
      {backgroundClass !== 'default' && (
        <div className={`background ${backgroundClass.replace(/\s+/g, '-').toLowerCase()} visible`}></div>
      )}

      {/* Previous background - keep visible during transitions */}
      {previousBackground && previousBackground !== 'default' && previousBackground !== backgroundClass && (
        <div className={`background previous ${previousBackground.replace(/\s+/g, '-').toLowerCase()} visible`}></div>
      )}

      <main className="main-content">
        <div className="search-container">
          <form onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search for a city..."
              value={city}
              onChange={(e) => setCity(e.target.value)}
            />
            <button type="submit">Search</button>
          </form>
        </div>

        {loading && (
          <div className="loading-overlay" key={loadingKey} ref={loadingRef}>
            <p>Fetching weather data...</p>
          </div>
        )}

        {error && (
          <div className="error">
            <p>Oops! {error}</p>
          </div>
        )}

        {weatherData && !loading && (
          <div className="weather-card">
            <h2>{weatherData.city}</h2>
            <div className="weather-info">
              <div className="temperature">
                {weatherData.temperature}°
              </div>
              <div className="description">{weatherData.description}</div>
              <div className="details">
                <p>Feels like<br/>{Math.round(weatherData.temperature * 0.95)}°</p>
                <p>Humidity<br/>{weatherData.humidity}%</p>
                <p>Wind<br/>{weatherData.windSpeed} km/h</p>
              </div>
            </div>
          </div>
        )}

        {!weatherData && !loading && !error && (
          <div className="empty-state">
            <p>Search for a city to see the weather</p>
          </div>
        )}
      </main>
    </div>
  );
};

export default MainContent;
