import React, { useState, useRef } from 'react';
import './css/MainContent.css';

const MainContent = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [prevWeather, setPrevWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const loadingRef = useRef(null);
  const [transitionActive, setTransitionActive] = useState(false);

  const handleSearch = (e) => {
    e.preventDefault();
    if (city.trim() === '') return;

    setLoading(true);
    setError(null);

    // Simulate API call
    setTimeout(() => {
      const descriptions = ['Sunny', 'Cloudy', 'Rainy', 'Partly Cloudy'];
      const description = descriptions[Math.floor(Math.random() * 4)];

      // Store previous weather for transition
      if (weatherData) {
        setPrevWeather(weatherData.description);
      }

      // Apply fade-out animation before removing the loading state
      if (loadingRef.current) {
        loadingRef.current.classList.add('fade-out');
        loadingRef.current.addEventListener('animationend', () => {
          setLoading(false);
          setTransitionActive(true);

          // Set new weather data
          setWeatherData({
            city: city,
            temperature: Math.floor(Math.random() * 30) + 5, // 5-35째C
            description: description,
            humidity: Math.floor(Math.random() * 50) + 30, // 30-80%
            windSpeed: Math.floor(Math.random() * 20) + 1, // 1-20 km/h
          });

          // Reset transition active after animation completes
          setTimeout(() => {
            setTransitionActive(false);
            setPrevWeather(null);
          }, 1500); // Match transition duration
        }, { once: true });
      } else {
        setLoading(false);
        setTransitionActive(true);

        setWeatherData({
          city: city,
          temperature: Math.floor(Math.random() * 30) + 5,
          description: description,
          humidity: Math.floor(Math.random() * 50) + 30,
          windSpeed: Math.floor(Math.random() * 20) + 1,
        });

        setTimeout(() => {
          setTransitionActive(false);
          setPrevWeather(null);
        }, 1500);
      }
    }, 1500);
  };

  return (
    <div className="app-container">
      {/* Previous weather background (fading out) */}
      {prevWeather && transitionActive && (
        <div 
          className="weather-background prev-weather" 
          data-weather={prevWeather}
        ></div>
      )}

      {/* Current weather background (fading in) */}
      <div 
        className={`weather-background current-weather ${transitionActive ? 'fade-in' : ''}`}
        data-weather={weatherData?.description}
      ></div>

      <main className="main-content">
        <div className="search-container">
          <form onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search for a city..."
              value={city}
              onChange={(e) => setCity(e.target.value)}
            />
            <button type="submit">Search</button>
          </form>
        </div>

        {loading && (
          <div className="loading-overlay" ref={loadingRef}>
            <p>Fetching weather data...</p>
          </div>
        )}

        {error && (
          <div className="error">
            <p>Oops! {error}</p>
          </div>
        )}

        {weatherData && !loading && (
          <div className="weather-card">
            <h2>{weatherData.city}</h2>
            <div className="weather-info">
              <div className="temperature">
                {weatherData.temperature}째
              </div>
              <div className="description">{weatherData.description}</div>
              <div className="details">
                <p>Feels like<br/>{Math.round(weatherData.temperature * 0.95)}째</p>
                <p>Humidity<br/>{weatherData.humidity}%</p>
                <p>Wind<br/>{weatherData.windSpeed} km/h</p>
              </div>
            </div>
          </div>
        )}

        {!weatherData && !loading && !error && (
          <div className="empty-state">
            <p>Search for a city to see the weather</p>
          </div>
        )}
      </main>
    </div>
  );
};

export default MainContent;
