import React, { useState, useEffect } from 'react';
import './css/MainContent.css';

const MainContent = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [weatherClass, setWeatherClass] = useState(null);

  const handleSearch = (e) => {
    e.preventDefault();
    if (city.trim() === '') return;

    setLoading(true);
    setError(null);
    // Reset weather class to trigger animation
    setWeatherClass(null);

    // Simulate API call
    setTimeout(() => {
      const descriptions = ['Sunny', 'Cloudy', 'Rainy', 'Partly Cloudy'];
      const description = descriptions[Math.floor(Math.random() * 4)];

      const newWeatherData = {
        city: city,
        temperature: Math.floor(Math.random() * 30) + 5, // 5-35째C
        description: description,
        humidity: Math.floor(Math.random() * 50) + 30, // 30-80%
        windSpeed: Math.floor(Math.random() * 20) + 1, // 1-20 km/h
      };

      setLoading(false);
      setWeatherData(newWeatherData);

      // Set the weather class after a brief delay to ensure animation triggers
      setTimeout(() => {
        setWeatherClass(description);
      }, 50);
    }, 1000);
  };

  return (
    <main className="main-content" data-weather={weatherClass}>
      <div className="search-container">
        <form onSubmit={handleSearch}>
          <input
            type="text"
            placeholder="Search for a city..."
            value={city}
            onChange={(e) => setCity(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
      </div>

      {loading && (
        <div className="loading">
          <p>Fetching weather data...</p>
        </div>
      )}

      {error && (
        <div className="error">
          <p>Oops! {error}</p>
        </div>
      )}

      {weatherData && !loading && (
        <div className="weather-card">
          <h2>{weatherData.city}</h2>
          <div className="weather-info">
            <div className="temperature">
              {weatherData.temperature}째
            </div>
            <div className="description">{weatherData.description}</div>
            <div className="details">
              <p>Feels like<br/>{Math.round(weatherData.temperature * 0.95)}째</p>
              <p>Humidity<br/>{weatherData.humidity}%</p>
              <p>Wind<br/>{weatherData.windSpeed} km/h</p>
            </div>
          </div>
        </div>
      )}

      {!weatherData && !loading && !error && (
        <div className="empty-state">
          <p>Search for a city to see the weather</p>
        </div>
      )}
    </main>
  );
};

export default MainContent;
