import React, { useState, useRef, useEffect } from 'react';
import './css/MainContent.css';

const MainContent = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const loadingRef = useRef(null);
  const [weatherKey, setWeatherKey] = useState(0); // Add a key to force re-render of weather styles

  const handleSearch = (e) => {
    e.preventDefault();
    if (city.trim() === '') return;

    setLoading(true);
    setError(null);

    // Simulate API call
    setTimeout(() => {
      const descriptions = ['Sunny', 'Cloudy', 'Rainy', 'Partly Cloudy'];
      const description = descriptions[Math.floor(Math.random() * 4)];

      // Apply fade-out animation before removing the loading state
      if (loadingRef.current) {
        loadingRef.current.classList.add('fade-out');
        loadingRef.current.addEventListener('animationend', () => {
          setLoading(false);

          // Increment weatherKey to force a style recalculation
          setWeatherKey(prevKey => prevKey + 1);

          setWeatherData({
            city: city,
            temperature: Math.floor(Math.random() * 30) + 5, // 5-35Â°C
            description: description,
            humidity: Math.floor(Math.random() * 50) + 30, // 30-80%
            windSpeed: Math.floor(Math.random() * 20) + 1, // 1-20 km/h
          });
        }, { once: true });
      } else {
        // Fallback in case ref isn't available
        setLoading(false);

        // Increment weatherKey to force a style recalculation
        setWeatherKey(prevKey => prevKey + 1);

        setWeatherData({
          city: city,
          temperature: Math.floor(Math.random() * 30) + 5,
          description: description,
          humidity: Math.floor(Math.random() * 50) + 30,
          windSpeed: Math.floor(Math.random() * 20) + 1,
        });
      }
    }, 1500); // Slightly longer to appreciate the animation
  };

  // Use effect to temporarily reset data-weather between changes
  useEffect(() => {
    if (weatherKey > 0) {
      const mainContent = document.querySelector('.main-content');
      if (mainContent) {
        // Temporarily remove the data-weather attribute to trigger transition
        mainContent.setAttribute('data-previous-weather', mainContent.getAttribute('data-weather') || '');
        mainContent.removeAttribute('data-weather');

        // Force browser to process the removal
        window.getComputedStyle(mainContent).opacity;

        // Add back the data-weather attribute
        mainContent.setAttribute('data-weather', weatherData?.description);
      }
    }
  }, [weatherKey, weatherData]);

  // Fix duplicate animation issue for loading spinner
  useEffect(() => {
    const fixSpinnerAnimation = () => {
      const style = document.createElement('style');
      style.innerHTML = `
        .loading-overlay:after {
          animation: spin 1s ease-in-out infinite, fadeIn 0.3s ease 0.1s forwards !important;
        }
      `;
      document.head.appendChild(style);
    };

    fixSpinnerAnimation();
  }, []);

  return (
    <main className="main-content" data-weather={weatherData?.description} key={`weather-container-${weatherKey}`}>
      <div className="search-container">
        <form onSubmit={handleSearch}>
          <input
            type="text"
            placeholder="Search for a city..."
            value={city}
            onChange={(e) => setCity(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
      </div>

      {loading && (
        <div className="loading-overlay" ref={loadingRef} key={`loading-${weatherKey}`}>
          <p>Fetching weather data...</p>
        </div>
      )}

      {error && (
        <div className="error">
          <p>Oops! {error}</p>
        </div>
      )}

      {weatherData && !loading && (
        <div className="weather-card">
          <h2>{weatherData.city}</h2>
          <div className="weather-info">
            <div className="temperature">
              {weatherData.temperature}Â°
            </div>
            <div className="description">{weatherData.description}</div>
            <div className="details">
              <p>Feels like<br/>{Math.round(weatherData.temperature * 0.95)}Â°</p>
              <p>Humidity<br/>{weatherData.humidity}%</p>
              <p>Wind<br/>{weatherData.windSpeed} km/h</p>
            </div>
          </div>
        </div>
      )}

      {!weatherData && !loading && !error && (
        <div className="empty-state">
          <p>Search for a city to see the weather</p>
        </div>
      )}
    </main>
  );
};

export default MainContent;
