import React, { useState, useRef, useEffect } from 'react';
import './css/MainContent.css';

const MainContent = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const loadingRef = useRef(null);
  const [searchCount, setSearchCount] = useState(0);
  const [backgroundClass, setBackgroundClass] = useState('default');
  const [transitioning, setTransitioning] = useState(false);
  const [previousBackground, setPreviousBackground] = useState('default');

  // This ensures the loading animation remounts with every search
  const loadingKey = `loading-${searchCount}`;

  // Effect to handle background transition when weather data changes
  useEffect(() => {
    if (weatherData) {
      setPreviousBackground(backgroundClass);
      setTransitioning(true);
      setBackgroundClass(weatherData.description);

      // Reset transitioning flag after animation completes
      const timer = setTimeout(() => {
        setTransitioning(false);
      }, 1500); // Match with CSS transition duration

      return () => clearTimeout(timer);
    }
  }, [weatherData, backgroundClass]);

  const handleSearch = (e) => {
    e.preventDefault();
    if (city.trim() === '') return;

    // First, always increment search count to ensure proper remounting
    setSearchCount(prevCount => prevCount + 1);

    // Save current background before loading
    const currentBackground = backgroundClass;

    // Then set loading state after a brief delay to ensure DOM updates
    setTimeout(() => {
      setLoading(true);
      setError(null);

      // Simulate API call with slightly longer delay
      setTimeout(() => {
        const descriptions = ['Sunny', 'Cloudy', 'Rainy', 'Partly Cloudy'];
        const description = descriptions[Math.floor(Math.random() * 4)];

        if (loadingRef.current) {
          // Add fade-out class
          loadingRef.current.classList.add('fade-out');

          // Listen for animation completion
          const handleAnimationEnd = () => {
            // Remove the event listener to avoid memory leaks
            if (loadingRef.current) {
              loadingRef.current.removeEventListener('animationend', handleAnimationEnd);
            }

            // Update state after animation completes
            setLoading(false);
            setPreviousBackground(currentBackground);
            setWeatherData({
              city: city,
              temperature: Math.floor(Math.random() * 30) + 5,
              description: description,
              humidity: Math.floor(Math.random() * 50) + 30,
              windSpeed: Math.floor(Math.random() * 20) + 1,
            });
          };

          // Add the event listener
          loadingRef.current.addEventListener('animationend', handleAnimationEnd);
        } else {
          // Fallback in case ref isn't available
          setLoading(false);
          setPreviousBackground(currentBackground);
          setWeatherData({
            city: city,
            temperature: Math.floor(Math.random() * 30) + 5,
            description: description,
            humidity: Math.floor(Math.random() * 50) + 30,
            windSpeed: Math.floor(Math.random() * 20) + 1,
          });
        }
      }, 1500);
    }, 10); // Small delay ensures React has completed previous state updates
  };

  // Determine which backgrounds to show
  const showDefaultBg = backgroundClass === 'default' || (previousBackground === 'default' && loading);
  const showPreviousBg = previousBackground !== 'default' && (loading || transitioning);
  const showCurrentBg = backgroundClass !== 'default' && !loading;

  return (
    <div className="wrapper">
      {/* Default background */}
      <div className={`background default ${!showDefaultBg ? 'fade-out' : ''}`}></div>

      {/* Previous background - keep during loading */}
      {previousBackground !== 'default' && previousBackground !== backgroundClass && (
        <div 
          className={`background ${previousBackground.replace(/\s+/g, '-').toLowerCase()} ${showPreviousBg ? 'visible' : ''}`}
        ></div>
      )}

      {/* Current background */}
      {backgroundClass !== 'default' && (
        <div 
          className={`background ${backgroundClass.replace(/\s+/g, '-').toLowerCase()} ${showCurrentBg && !transitioning ? 'visible' : ''}`}
        ></div>
      )}

      <main className="main-content">
        <div className="search-container">
          <form onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search for a city..."
              value={city}
              onChange={(e) => setCity(e.target.value)}
            />
            <button type="submit">Search</button>
          </form>
        </div>

        {loading && (
          <div className="loading-overlay" key={loadingKey} ref={loadingRef}>
            <p>Fetching weather data...</p>
          </div>
        )}

        {error && (
          <div className="error">
            <p>Oops! {error}</p>
          </div>
        )}

        {weatherData && !loading && (
          <div className="weather-card">
            <h2>{weatherData.city}</h2>
            <div className="weather-info">
              <div className="temperature">
                {weatherData.temperature}°
              </div>
              <div className="description">{weatherData.description}</div>
              <div className="details">
                <p>Feels like<br/>{Math.round(weatherData.temperature * 0.95)}°</p>
                <p>Humidity<br/>{weatherData.humidity}%</p>
                <p>Wind<br/>{weatherData.windSpeed} km/h</p>
              </div>
            </div>
          </div>
        )}

        {!weatherData && !loading && !error && (
          <div className="empty-state">
            <p>Search for a city to see the weather</p>
          </div>
        )}
      </main>
    </div>
  );
};

export default MainContent;
